bd540f3f-3612-467e-abc3-ba113b53a32d	81712945-zh-Hans.srt	[{"id":1,"startTime":"00:01:47,520","endTime":"00:01:49,280","text":"“任安乐一袭红衣","libreTranslation":"\\"Ren Anle mặc đồ đỏ","opusTranslation":"♪ Chúng ta sẽ đánh nhau và chiến đấu ♪","mbartTranslation":"\\"Nan-lơi-tông\\"","nllbTranslation":"Khi Nhậm An Lạc tấn công áo đỏ,","selectedModel":"nllb","translation":"Khi Nhậm An Lạc tấn công áo đỏ,"},{"id":2,"startTime":"00:01:49,760","endTime":"00:01:52,240","text":"与太子殿下十指相扣","libreTranslation":"Những ngón tay nối liền với Thái tử điện hạ","opusTranslation":"- Sao?","mbartTranslation":"Chắt chặt 10 ngón tay với hoàng tử","nllbTranslation":"và Thái Tử điện hạ.","selectedModel":"nllb","translation":"và Thái Tử điện hạ."},{"id":3,"startTime":"00:01:52,720","endTime":"00:01:56,000","text":"又含情脉脉地看着他英俊的脸颊","libreTranslation":"Và nhìn khuôn mặt đẹp trai của anh một cách trìu mến","opusTranslation":"Anh ta có mạch đập để nhìn lên má đẹp trai.","mbartTranslation":"và nhìn vào khuôn mặt xinh đẹp của anh ta","nllbTranslation":"lại đeo lòng nhìn mặt anh tuấn của hắn.","selectedModel":"nllb","translation":"lại đeo lòng nhìn mặt anh tuấn của hắn."},{"id":4,"startTime":"00:01:56,080","endTime":"00:01:56,960","text":"然后说道…”","libreTranslation":"Sau đó anh ấy nói...\\"","opusTranslation":"Và nói rằng...","mbartTranslation":"Và rồi tôi nói...\\"","nllbTranslation":"rồi nói rằng...","selectedModel":"nllb","translation":"rồi nói rằng..."},{"id":5,"startTime":"00:01:57,040","endTime":"00:01:58,320","text":"“公子莫怕","libreTranslation":"“Sư phụ, đừng sợ","opusTranslation":"Không được sợ nguy hiểm!","mbartTranslation":"\\"Cô không sợ","nllbTranslation":"Ngụy công tử đừng sợ.","selectedModel":"nllb","translation":"Ngụy công tử đừng sợ."},{"id":6,"startTime":"00:01:58,400","endTime":"00:01:59,880","text":"现在既已上岸","libreTranslation":"Giờ đã lên bờ rồi","opusTranslation":"Bây giờ chúng ta đã vào bờ.","mbartTranslation":"Bây giờ đã lên bờ","nllbTranslation":"Bây giờ đã lên bờ,","selectedModel":"nllb","translation":"Bây giờ đã lên bờ,"},{"id":7,"startTime":"00:01:59,960","endTime":"00:02:02,120","text":"你我身后便是安乐寨","libreTranslation":"Đằng sau bạn và tôi là Anlezhai","opusTranslation":"Đằng sau ta là nhà tù an toàn.","mbartTranslation":"You're behind me","nllbTranslation":"Sau hai ta là An Lạc Tể.","selectedModel":"nllb","translation":"Sau hai ta là An Lạc Tể."},{"id":8,"startTime":"00:02:02,200","endTime":"00:02:04,000","text":"从此以后 再也无人…”","libreTranslation":"Từ nay về sau sẽ không có ai...\\"","opusTranslation":"Và không còn ai... bị... bị...","mbartTranslation":"Vào sau đó, sẽ không có ai ...","nllbTranslation":"Từ nay về sau, sẽ không còn ai...","selectedModel":"nllb","translation":"Từ nay về sau, sẽ không còn ai..."},{"id":9,"startTime":"00:02:04,680","endTime":"00:02:06,600","text":"吵吵闹闹 成何体统？","libreTranslation":"Làm thế nào là thích hợp để gây ra tiếng ồn?","opusTranslation":"Chuyện ồn ào gì?","mbartTranslation":"Giọng như thế nào?","nllbTranslation":"Cha, thành thân thống gì?","selectedModel":"nllb","translation":"Cha, thành thân thống gì?"},{"id":10,"startTime":"00:02:19,800","endTime":"00:02:20,920","text":"什么东西啊？","libreTranslation":"Nó là gì?","opusTranslation":"Cái gì thế?","mbartTranslation":"Cái gì?","nllbTranslation":"Cái gì?","selectedModel":"nllb","translation":"Cái gì?"},{"id":11,"startTime":"00:02:21,000","endTime":"00:02:22,080","text":"给朕瞧瞧","libreTranslation":"Cho tôi xem","opusTranslation":"Cho trẫm xem đi","mbartTranslation":"Hãy nhìn tôi","nllbTranslation":"Cho cha.","selectedModel":"nllb","translation":"Cho cha."},{"id":12,"startTime":"00:02:28,080","endTime":"00:02:30,400","text":"“《女匪与太子》”？","libreTranslation":"\\"\\"Nữ cướp và hoàng tử\\"?","opusTranslation":"Tiểu Ngọc và Tiểu Phong?","mbartTranslation":"\\"Nguy Thiệt và Hoàng tử\\"?","nllbTranslation":"Cô cô cô và thái tử cô cô?","selectedModel":"nllb","translation":"Cô cô cô và thái tử cô cô?"},{"id":13,"startTime":"00:02:39,440","endTime":"00:02:41,280","text":"话说那任安乐","libreTranslation":"Hãy nói về Ren Anle","opusTranslation":"Dù sao thì, đó cũng là một cách để giải tỏa tâm lý.","mbartTranslation":"Giọng nói đó là An-lơ","nllbTranslation":"Nói ra Nhậm An Lạc","selectedModel":"nllb","translation":"Nói ra Nhậm An Lạc"},{"id":14,"startTime":"00:02:41,760","endTime":"00:02:44,680","text":"一夕之间名动京城","libreTranslation":"Trở nên nổi tiếng ở Bắc Kinh chỉ sau một đêm","opusTranslation":"Một đêm nọ, họ nổi tiếng với kinh thành.","mbartTranslation":"Thành phố lộng lẫy giữa ban đêm","nllbTranslation":"một đêm giữa hai ngày,","selectedModel":"nllb","translation":"một đêm giữa hai ngày,"},{"id":15,"startTime":"00:02:45,160","endTime":"00:02:48,960","text":"街头巷尾谁不在问 谁不在说？","libreTranslation":"Ai không hỏi hoặc nói chuyện trên đường phố?","opusTranslation":"Ai không ở cuối con đường? Ai nói?","mbartTranslation":"Ai không hỏi ai không nói?","nllbTranslation":"Ai không hỏi, ai không nói?","selectedModel":"nllb","translation":"Ai không hỏi, ai không nói?"},{"id":16,"startTime":"00:02:49,440","endTime":"00:02:50,720","text":"狩猎宴那日","libreTranslation":"Ngày lễ săn bắn","opusTranslation":"Ngày đi săn.","mbartTranslation":"The Day of Hunting","nllbTranslation":"Hôm tiệc săn bắn,","selectedModel":"nllb","translation":"Hôm tiệc săn bắn,"},{"id":17,"startTime":"00:02:50,800","endTime":"00:02:53,880","text":"眼见那任安乐一人一马","libreTranslation":"Nhìn thấy Ren Anle có một người một ngựa","opusTranslation":"Nhìn thấy tên này một mình","mbartTranslation":"Con An-lơm nhìn qua từng chiếc xe","nllbTranslation":"Nhìn thấy Nhậm An Lạc một mình một ngựa,","selectedModel":"nllb","translation":"Nhìn thấy Nhậm An Lạc một mình một ngựa,"},{"id":18,"startTime":"00:02:53,960","endTime":"00:02:55,480","text":"笑靥如花","libreTranslation":"Cười như một bông hoa","opusTranslation":"Cười như hoa.","mbartTranslation":"Giọng cười như hoa","nllbTranslation":"cười ngốc như hoa.","selectedModel":"nllb","translation":"cười ngốc như hoa."},{"id":19,"startTime":"00:02:55,560","endTime":"00:02:58,440","text":"直奔咱们太子殿下而来","libreTranslation":"Hãy đến thẳng với Thái tử của chúng ta","opusTranslation":"Hãy đi thẳng đến hoàng tử của chúng ta.","mbartTranslation":"Bắt đầu đến ngay đây, Thượng hoàng","nllbTranslation":"thẳng về phía Thái Tử điện hạ.","selectedModel":"nllb","translation":"thẳng về phía Thái Tử điện hạ."},{"id":20,"startTime":"00:02:59,760","endTime":"00:03:04,320","text":"“我任安乐是来当太子妃的”","libreTranslation":"“Tôi, Ren Anle, đến đây để làm công chúa.”","opusTranslation":"Ta sinh ra để làm hoàng hậu.","mbartTranslation":"\\"I'm appointed là nữ hoàng\\"","nllbTranslation":"Nhậm An Lạc ta là con ruột của thái tử phi.","selectedModel":"nllb","translation":"Nhậm An Lạc ta là con ruột của thái tử phi."},{"id":21,"startTime":"00:03:04,400","endTime":"00:03:05,800","text":"-好\\n-好","libreTranslation":"-Tốt","opusTranslation":"- Được rồi.","mbartTranslation":"- Tốt.","nllbTranslation":"- Hay! - Hay!","selectedModel":"nllb","translation":"- Hay! - Hay!"},{"id":22,"startTime":"00:03:08,400","endTime":"00:03:09,640","text":"-来\\n-殿下","libreTranslation":"-Đến đây\\n-Hoàng thân","opusTranslation":"- Đi nào, thưa ngài.","mbartTranslation":"-Thưa quý ông","nllbTranslation":"- Nào. - Điện hạ.","selectedModel":"nllb","translation":"- Nào. - Điện hạ."},{"id":23,"startTime":"00:03:09,720","endTime":"00:03:11,200","text":"-咱们接着讲\\n-你说那日狩猎宴上","libreTranslation":"- Hãy tiếp tục\\n-Anh đã nói thế trong bữa tiệc săn ngày hôm đó.","opusTranslation":"- Anh nói là bữa tiệc săn.","mbartTranslation":"-Let's go tiếp theo. -You said at the hunting party that day","nllbTranslation":"- Chúng ta tiếp tục. - Hôm đó,","selectedModel":"nllb","translation":"- Chúng ta tiếp tục. - Hôm đó,"},{"id":24,"startTime":"00:03:11,280","endTime":"00:03:12,320","text":"都是有头有脸的人","libreTranslation":"Họ đều là những người đáng kính","opusTranslation":"Những người có gương mặt thiên tài.","mbartTranslation":"Mọi người đều có mặt","nllbTranslation":"đều là người có đầu có mặt.","selectedModel":"nllb","translation":"đều là người có đầu có mặt."},{"id":25,"startTime":"00:03:12,400","endTime":"00:03:13,760","text":"是谁把这种事往外说？","libreTranslation":"Ai đã nói ra chuyện này ở bên ngoài?","opusTranslation":"Ai đã làm chuyện này?","mbartTranslation":"Ai đã nói về điều này?","nllbTranslation":"Ai nói ra chuyện này?","selectedModel":"nllb","translation":"Ai nói ra chuyện này?"},{"id":26,"startTime":"00:03:14,640","endTime":"00:03:17,360","text":"里面可有个肆无忌惮的草莽英雄？","libreTranslation":"Có một anh hùng vô đạo đức và liều lĩnh bên trong?","opusTranslation":"Có một anh hùng hùng tự do trong đó.","mbartTranslation":"Có một anh hùng khôn ngoan?","nllbTranslation":"Trong đó có một anh hùng cỏ dại vô sự không?","selectedModel":"nllb","translation":"Trong đó có một anh hùng cỏ dại vô sự không?"},{"id":27,"startTime":"00:03:18,280","endTime":"00:03:20,920","text":"你说的是任安乐本尊？","libreTranslation":"Bạn đang nói về Ren Anle?","opusTranslation":"Ý anh là một người bạn tốt?","mbartTranslation":"You're talking about Niêm Leung?","nllbTranslation":"Ngươi đang nói là Nhậm An Lạc bản tôn?","selectedModel":"nllb","translation":"Ngươi đang nói là Nhậm An Lạc bản tôn?"},{"id":28,"startTime":"00:03:21,680","endTime":"00:03:24,520","text":"那她这么做是图什么呢？","libreTranslation":"Vậy tại sao cô ấy lại làm điều này?","opusTranslation":"Vậy cô ta làm gì?","mbartTranslation":"What did she do?","nllbTranslation":"Vậy cô ta làm vậy là mưu đồ gì?","selectedModel":"nllb","translation":"Vậy cô ta làm vậy là mưu đồ gì?"},{"id":29,"startTime":"00:03:25,000","endTime":"00:03:27,360","text":"她虽草莽 但却聪明","libreTranslation":"Tuy cô ấy liều lĩnh nhưng cô ấy rất thông minh","opusTranslation":"Cô ấy rất ngoan, nhưng thông minh.","mbartTranslation":"She dũng cảm nhưng lại thông minh","nllbTranslation":"Tuy cô ta ngốc nhưng lại thông minh,","selectedModel":"nllb","translation":"Tuy cô ta ngốc nhưng lại thông minh,"},{"id":30,"startTime":"00:03:27,440","endTime":"00:03:28,520","text":"甫入京城","libreTranslation":"Vừa vào thủ đô","opusTranslation":"Tito đã vào kinh thành.","mbartTranslation":"Fu-in-kyo","nllbTranslation":"vào kinh thành.","selectedModel":"nllb","translation":"vào kinh thành."},{"id":31,"startTime":"00:03:28,600","endTime":"00:03:30,640","text":"便懂得利用民意、人心造势","libreTranslation":"Khi đó bạn mới biết lợi dụng dư luận và lòng dân để tạo đà","opusTranslation":"Bạn biết cách sử dụng ý chí, lòng nhân đạo.","mbartTranslation":"Cũng có thể sử dụng ý kiến và con người để tạo nên sức mạnh","nllbTranslation":"thì biết lợi dụng ý dân, tạo thế tâm nhân.","selectedModel":"nllb","translation":"thì biết lợi dụng ý dân, tạo thế tâm nhân."},{"id":32,"startTime":"00:03:33,360","endTime":"00:03:34,320","text":"原来如此啊","libreTranslation":"Thế là xong","opusTranslation":"Hiểu rồi.","mbartTranslation":"Ồ, đúng vậy","nllbTranslation":"Thì ra là vậy.","selectedModel":"nllb","translation":"Thì ra là vậy."},{"id":33,"startTime":"00:03:34,800","endTime":"00:03:35,760","text":"她可真是厉害啊","libreTranslation":"Cô ấy thực sự tuyệt vời","opusTranslation":"Cô ta giỏi thật đấy.","mbartTranslation":"She's really strong","nllbTranslation":"Cô ấy thật là giỏi.","selectedModel":"nllb","translation":"Cô ấy thật là giỏi."},{"id":34,"startTime":"00:03:35,840","endTime":"00:03:37,440","text":"只可惜没用到正途","libreTranslation":"Thật đáng tiếc là nó đã không được sử dụng đúng cách.","opusTranslation":"Nhưng không may là nó không có thật.","mbartTranslation":"Tiếc thay, nó không được sử dụng đúng cách","nllbTranslation":"chỉ tiếc là không dùng được đường chính.","selectedModel":"nllb","translation":"chỉ tiếc là không dùng được đường chính."},{"id":35,"startTime":"00:03:38,600","endTime":"00:03:40,560","text":"就是 干点别的什么不好","libreTranslation":"Chỉ là làm việc khác là không tốt.","opusTranslation":"Chỉ là, làm những việc khác không tốt lắm.","mbartTranslation":"Tốt nhất là làm điều gì đó tồi","nllbTranslation":"Đúng vậy. Làm gì khác cũng không ổn.","selectedModel":"nllb","translation":"Đúng vậy. Làm gì khác cũng không ổn."},{"id":36,"startTime":"00:03:40,640","endTime":"00:03:42,280","text":"非要拿来羞辱我们家太子殿下？","libreTranslation":"Chẳng lẽ phải dùng nó để sỉ nhục Thái tử của chúng ta sao?","opusTranslation":"Muốn làm nhục hoàng tử của chúng ta sao?","mbartTranslation":"Don't shame the Prince của chúng ta?","nllbTranslation":"phải mang đến để sỉ nhục Thái Tử điện hạ nhà ta?","selectedModel":"nllb","translation":"phải mang đến để sỉ nhục Thái Tử điện hạ nhà ta?"},{"id":37,"startTime":"00:03:47,520","endTime":"00:03:48,760","text":"民间多忘事","libreTranslation":"Người ta quên nhiều thứ","opusTranslation":"Dân chúng có nhiều chuyện quên lãng.","mbartTranslation":"Mấu chuyện dân sự","nllbTranslation":"dân gian quên mất nhiều chuyện,","selectedModel":"nllb","translation":"dân gian quên mất nhiều chuyện,"},{"id":38,"startTime":"00:03:49,440","endTime":"00:03:51,080","text":"她若不继续生事","libreTranslation":"Nếu cô ấy không tiếp tục gây rắc rối","opusTranslation":"Nếu cô ấy không tiếp tục làm việc của mình","mbartTranslation":"Nếu she không sống","nllbTranslation":"Nếu cô ấy không tiếp tục sống,","selectedModel":"nllb","translation":"Nếu cô ấy không tiếp tục sống,"},{"id":39,"startTime":"00:03:51,160","endTime":"00:03:52,680","text":"等过阵子便风平浪静了","libreTranslation":"Sau một thời gian, mọi thứ sẽ lắng xuống.","opusTranslation":"Sau này phong thủy yên tĩnh.","mbartTranslation":"Chàng qua, the wind calm","nllbTranslation":"Đợi một lát thì gió bình tĩnh.","selectedModel":"nllb","translation":"Đợi một lát thì gió bình tĩnh."},{"id":40,"startTime":"00:03:53,720","endTime":"00:03:55,320","text":"但按照她的性子","libreTranslation":"Nhưng theo tính cách của cô ấy","opusTranslation":"Nhưng theo tính cách của cô ấy,","mbartTranslation":"Nhưng theo giới tính","nllbTranslation":"Nhưng theo tính cách của cô ấy,","selectedModel":"nllb","translation":"Nhưng theo tính cách của cô ấy,"},{"id":41,"startTime":"00:03:55,400","endTime":"00:03:57,520","text":"她怎么会不接着兴风作浪？","libreTranslation":"Làm sao cô có thể không tiếp tục gây rắc rối?","opusTranslation":"Sao cô ấy không đi dạo?","mbartTranslation":"Làm sao cô không tiếp tục nổi lên?","nllbTranslation":"sao cô ấy không tiếp theo Hưng Phong làm sóng?","selectedModel":"nllb","translation":"sao cô ấy không tiếp theo Hưng Phong làm sóng?"},{"id":42,"startTime":"00:03:59,200","endTime":"00:04:00,480","text":"我会想办法","libreTranslation":"Tôi sẽ nghĩ ra cách","opusTranslation":"Tôi sẽ tìm cách.","mbartTranslation":"Tôi sẽ nghĩ ra một cách","nllbTranslation":"Ta sẽ nghĩ cách.","selectedModel":"nllb","translation":"Ta sẽ nghĩ cách."},{"id":43,"startTime":"00:04:01,720","endTime":"00:04:03,040","text":"不让她荒唐太久","libreTranslation":"Đừng để cô ấy buồn cười quá lâu","opusTranslation":"Không quá lâu để nó không phát điên.","mbartTranslation":"Don't let her qua đời","nllbTranslation":"không để cô ấy hoang đường quá lâu.","selectedModel":"nllb","translation":"không để cô ấy hoang đường quá lâu."},{"id":44,"startTime":"00:04:10,040","endTime":"00:04:11,040","text":"小姐","libreTranslation":"Cô","opusTranslation":"Thưa cô.","mbartTranslation":"Cô","nllbTranslation":"Tiểu thư.","selectedModel":"nllb","translation":"Tiểu thư."},{"id":45,"startTime":"00:04:11,120","endTime":"00:04:14,240","text":"这座宅子离太子府只有咫尺之遥","libreTranslation":"Ngôi nhà này chỉ cách Dinh thự của Hoàng tử một quãng đường ngắn","opusTranslation":"Ngôi nhà này cách đây vài mét.","mbartTranslation":"The toà nhà này chỉ cách Prince Charming","nllbTranslation":"Nhà này chỉ cách phủ Thái tử một dặm.","selectedModel":"nllb","translation":"Nhà này chỉ cách phủ Thái tử một dặm."},{"id":46,"startTime":"00:04:14,320","endTime":"00:04:17,839","text":"西行不远便是三省六部与九寺五监","libreTranslation":"Cách đó không xa về phía Tây là Tam Châu, Lục Bộ và Cửu Tự Ngũ Tù.","opusTranslation":"Không xa phía Tây là San Diego và Ngũ Tự.","mbartTranslation":"Không xa đó là 6 bộ ban tri và 5 bộ ban 9寺","nllbTranslation":"Tây không xa là sáu bộ tam tỉnh và Ngũ giám của Cửu tự.","selectedModel":"nllb","translation":"Tây không xa là sáu bộ tam tỉnh và Ngũ giám của Cửu tự."},{"id":47,"startTime":"00:04:18,720","endTime":"00:04:20,800","text":"此外 还有一条密道","libreTranslation":"Ngoài ra còn có một lối đi bí mật","opusTranslation":"Thêm vào đó, còn có một đường hầm bí mật.","mbartTranslation":"Và một cách bí mật","nllbTranslation":"Ngoài ra còn có một mật đạo.","selectedModel":"nllb","translation":"Ngoài ra còn có một mật đạo."},{"id":48,"startTime":"00:04:22,280","endTime":"00:04:23,560","text":"他果然心细","libreTranslation":"Anh ấy thực sự chu đáo","opusTranslation":"Anh ấy đã làm rất tốt.","mbartTranslation":"Ông thật sự là một người có tâm","nllbTranslation":"Quả nhiên hắn rất tinh tế.","selectedModel":"nllb","translation":"Quả nhiên hắn rất tinh tế."},{"id":49,"startTime":"00:04:23,640","endTime":"00:04:25,440","text":"将这一切都已经安排好了","libreTranslation":"Tất cả điều này đã được sắp xếp","opusTranslation":"Mọi chuyện đã được sắp xếp.","mbartTranslation":"Mọi thứ đã được sắp xếp","nllbTranslation":"sắp xếp xong mọi chuyện rồi.","selectedModel":"nllb","translation":"sắp xếp xong mọi chuyện rồi."},{"id":50,"startTime":"00:04:27,040","endTime":"00:04:28,040","text":"小姐","libreTranslation":"Cô","opusTranslation":"Thưa cô.","mbartTranslation":"Cô","nllbTranslation":"Tiểu thư.","selectedModel":"nllb","translation":"Tiểu thư."},{"id":51,"startTime":"00:04:28,120","endTime":"00:04:29,880","text":"你在狩猎场求嫁太子的事情","libreTranslation":"Chuyện gì đã xảy ra khi bạn cầu hôn hoàng tử ở bãi săn?","opusTranslation":"Việc anh cầu hôn thái hậu ở trường bắn.","mbartTranslation":"What you're doing on the hunt for the queen","nllbTranslation":"Chuyện cô cầu hôn Thái tử ở bãi săn","selectedModel":"nllb","translation":"Chuyện cô cầu hôn Thái tử ở bãi săn"},{"id":52,"startTime":"00:04:29,960","endTime":"00:04:31,360","text":"外面都传疯了","libreTranslation":"Bên ngoài đang điên cuồng","opusTranslation":"Ngoài kia điên hết rồi.","mbartTranslation":"Mọi người ra ngoài đều bị điên","nllbTranslation":"Bên ngoài đều lan truyền điên.","selectedModel":"nllb","translation":"Bên ngoài đều lan truyền điên."},{"id":53,"startTime":"00:04:31,440","endTime":"00:04:32,760","text":"这酒馆里说戏的","libreTranslation":"Bi kịch ở quán rượu này","opusTranslation":"Trong quán rượu này, họ nói thế.","mbartTranslation":"Người nói chuyện trong quán","nllbTranslation":"Trong quán rượu này nói kịch,","selectedModel":"nllb","translation":"Trong quán rượu này nói kịch,"},{"id":54,"startTime":"00:04:32,840","endTime":"00:04:34,280","text":"茶馆里热议的","libreTranslation":"Thảo luận sôi nổi trong quán trà","opusTranslation":"Tôi đã chuẩn bị cho trà quán.","mbartTranslation":"trong tiệm.","nllbTranslation":"trong quán trà bàn tán nóng.","selectedModel":"nllb","translation":"trong quán trà bàn tán nóng."},{"id":55,"startTime":"00:04:34,840","endTime":"00:04:37,200","text":"你看 还有这么些个话本子","libreTranslation":"Nhìn xem, có rất nhiều sách truyện","opusTranslation":"Nhìn này, vẫn còn nhiều thứ để nói đấy.","mbartTranslation":"\\"You see, there's more than 1 cuốn sách","nllbTranslation":"Cô xem, còn có mấy câu này nữa.","selectedModel":"nllb","translation":"Cô xem, còn có mấy câu này nữa."},{"id":56,"startTime":"00:04:37,280","endTime":"00:04:40,400","text":"咱们在京城那可是一举成名了","libreTranslation":"Chúng tôi trở nên nổi tiếng chỉ trong một lần ở Bắc Kinh.","opusTranslation":"Chúng ta đang đứng trên đường của kinh thành.","mbartTranslation":"Chúng tôi đã thành công trong thành phố京","nllbTranslation":"Chúng ta đã thành danh ở kinh thành một lần rồi.","selectedModel":"nllb","translation":"Chúng ta đã thành danh ở kinh thành một lần rồi."},{"id":57,"startTime":"00:04:43,840","endTime":"00:04:45,840","text":"这些话本子虽然看起来很肉麻","libreTranslation":"Mặc dù những từ này trong cuốn sách trông rất kinh tởm,","opusTranslation":"Những lời này có vẻ rất hay.","mbartTranslation":"Những câu chuyện có vẻ rất thô sơ","nllbTranslation":"Mặc dù những lời này vốn có vẻ rất ngây thơ,","selectedModel":"nllb","translation":"Mặc dù những lời này vốn có vẻ rất ngây thơ,"},{"id":58,"startTime":"00:04:45,920","endTime":"00:04:47,160","text":"但的确是有些用处的","libreTranslation":"Nhưng nó có một số công dụng","opusTranslation":"Nhưng cũng có ích đấy.","mbartTranslation":"Nhưng thực sự có một vài lợi ích","nllbTranslation":"nhưng quả thật có ích.","selectedModel":"nllb","translation":"nhưng quả thật có ích."},{"id":59,"startTime":"00:04:49,360","endTime":"00:04:52,080","text":"可这百姓们好歹还有个响儿","libreTranslation":"Nhưng ít nhất người dân bình thường vẫn có tiếng nói.","opusTranslation":"Nhưng ở đây mọi người ít nhất cũng còn có một tiếng nói.","mbartTranslation":"Nhưng những người này lại có một tiếng nói","nllbTranslation":"Nhưng dân chúng này cũng rất thẹn, còn có một tiếng động.","selectedModel":"nllb","translation":"Nhưng dân chúng này cũng rất thẹn, còn có một tiếng động."},{"id":60,"startTime":"00:04:52,160","endTime":"00:04:53,960","text":"哪像那些大臣们啊？","libreTranslation":"Họ giống những bộ trưởng đó thế nào?","opusTranslation":"Các vị thần đâu có giống các quý ông đó?","mbartTranslation":"Những bộ trưởng như thế nào?","nllbTranslation":"chẳng giống mấy vị tướng khác đâu.","selectedModel":"nllb","translation":"chẳng giống mấy vị tướng khác đâu."},{"id":61,"startTime":"00:04:54,040","endTime":"00:04:56,080","text":"他们可收了咱们的银子","libreTranslation":"Họ có lấy tiền của chúng tôi không?","opusTranslation":"Họ đã có tiền của chúng ta.","mbartTranslation":"Họ có thể thu về tiền của chúng ta","nllbTranslation":"Họ đã nhận được tiền của chúng ta.","selectedModel":"nllb","translation":"Họ đã nhận được tiền của chúng ta."},{"id":62,"startTime":"00:04:56,160","endTime":"00:04:58,160","text":"按道上的规矩来说","libreTranslation":"Theo quy định trên đường","opusTranslation":"Theo quy tắc đường phố,","mbartTranslation":"For the rules on the road","nllbTranslation":"Theo quy tắc đạo đức,","selectedModel":"nllb","translation":"Theo quy tắc đạo đức,"},{"id":63,"startTime":"00:04:58,240","endTime":"00:04:59,600","text":"这可是买路钱","libreTranslation":"Đây là tiền mua đường","opusTranslation":"Đây là đường đi.","mbartTranslation":"Đây là tiền để đi","nllbTranslation":"Đây là tiền mua đường.","selectedModel":"nllb","translation":"Đây là tiền mua đường."},{"id":64,"startTime":"00:04:59,680","endTime":"00:05:01,960","text":"结果他们居然连大门都没让我们进","libreTranslation":"Kết quả là họ thậm chí không cho chúng tôi vào qua cổng.","opusTranslation":"Họ không cho chúng tôi vào.","mbartTranslation":"They didn't even let us in","nllbTranslation":"Kết quả là họ không cho bọn ta vào cả cửa.","selectedModel":"nllb","translation":"Kết quả là họ không cho bọn ta vào cả cửa."},{"id":65,"startTime":"00:05:02,040","endTime":"00:05:03,920","text":"这个亏可吃大了","libreTranslation":"Đây là một mất mát lớn","opusTranslation":"Thế thì đúng là quá đủ rồi.","mbartTranslation":"Lấy nhiều mất mát","nllbTranslation":"Lợi hại này đáng quá.","selectedModel":"nllb","translation":"Lợi hại này đáng quá."},{"id":66,"startTime":"00:05:06,320","endTime":"00:05:07,920","text":"难怪小姐说你没出息","libreTranslation":"Thảo nào cô ấy nói bạn không có tương lai","opusTranslation":"Không ngạc nhiên là tiểu thư nói cậu không có tài.","mbartTranslation":"Không có gì ngạc nhiên khi cô nói rằng cô không thể thở được","nllbTranslation":"Thảo nào tiểu thư nói cô không nghỉ ngơi.","selectedModel":"nllb","translation":"Thảo nào tiểu thư nói cô không nghỉ ngơi."},{"id":67,"startTime":"00:05:08,400","endTime":"00:05:10,200","text":"咱们的安排奏效了","libreTranslation":"Sự sắp xếp của chúng tôi đã có hiệu quả","opusTranslation":"Chúng ta đã có một thỏa thuận.","mbartTranslation":"Our arrangements work","nllbTranslation":"sắp xếp của chúng ta đã có tác dụng.","selectedModel":"nllb","translation":"sắp xếp của chúng ta đã có tác dụng."},{"id":68,"startTime":"00:05:11,320","endTime":"00:05:12,600","text":"我们初入京城","libreTranslation":"Lần đầu tiên chúng tôi đến Bắc Kinh","opusTranslation":"Chúng ta mới bắt đầu cuộc xâm lược.","mbartTranslation":"Lần đầu tiên chúng tôi đến thành phố","nllbTranslation":"Chúng ta mới vào kinh thành.","selectedModel":"nllb","translation":"Chúng ta mới vào kinh thành."},{"id":69,"startTime":"00:05:12,680","endTime":"00:05:14,720","text":"他们肯收东西已是不错了","libreTranslation":"Thật tốt khi họ sẵn sàng chấp nhận mọi thứ.","opusTranslation":"Họ đã sẵn sàng thu dọn mọi thứ.","mbartTranslation":"They've been willing to accept.","nllbTranslation":"Họ chịu nhận đồ thì tốt rồi.","selectedModel":"nllb","translation":"Họ chịu nhận đồ thì tốt rồi."},{"id":70,"startTime":"00:05:14,800","endTime":"00:05:16,480","text":"韩仲远待小姐态度不明","libreTranslation":"Thái độ của Hàn Trung Nguyên đối với phu nhân không rõ ràng","opusTranslation":"Hàn Trọng không biết thái độ của tiểu thư.","mbartTranslation":"Hoan Chung-hẹn đối xử với cô gái không rõ","nllbTranslation":"Hàn Trọng Viễn giữ thái độ của tiểu thư không rõ ràng.","selectedModel":"nllb","translation":"Hàn Trọng Viễn giữ thái độ của tiểu thư không rõ ràng."},{"id":71,"startTime":"00:05:16,560","endTime":"00:05:19,360","text":"他们此时是不敢贸然同我们结交的","libreTranslation":"Họ không dám kết bạn với chúng ta một cách hấp tấp vào lúc này.","opusTranslation":"Họ không đủ khả năng làm thế.","mbartTranslation":"Vào lúc đó, họ đã không dám giao tiếp với chúng ta","nllbTranslation":"Lúc này họ không dám tự do kết giao với chúng ta.","selectedModel":"nllb","translation":"Lúc này họ không dám tự do kết giao với chúng ta."},{"id":72,"startTime":"00:05:20,240","endTime":"00:05:21,240","text":"无妨","libreTranslation":"Không có gì","opusTranslation":"Không có gì.","mbartTranslation":"Không có gì cả","nllbTranslation":"Không sao.","selectedModel":"nllb","translation":"Không sao."},{"id":73,"startTime":"00:05:21,800","endTime":"00:05:22,880","text":"大路朝天","libreTranslation":"Con đường hướng về phía bầu trời","opusTranslation":"Đường phố lộ thiên","mbartTranslation":"Đại lộ trên trời","nllbTranslation":"Đại lộ đến trời","selectedModel":"nllb","translation":"Đại lộ đến trời"},{"id":74,"startTime":"00:05:23,360","endTime":"00:05:25,160","text":"合该为我任安乐而开","libreTranslation":"Nó nên mở ra cho sự bình yên và hạnh phúc của tôi","opusTranslation":"Tôi sẽ mở cho anh.","mbartTranslation":"Cho tôi an ủi","nllbTranslation":"nên mở cho Nhậm An Lạc ta.","selectedModel":"nllb","translation":"nên mở cho Nhậm An Lạc ta."},{"id":75,"startTime":"00:05:26,160","endTime":"00:05:29,040","text":"再说我任安乐的嫁妆是三万水师","libreTranslation":"Ngoài ra, của hồi môn của tôi Nhậm An Lạc là 30.000 quân hải quân.","opusTranslation":"Hơn nữa, tôi lấy làm hạnh phúc với 30 ngàn thủy sư.","mbartTranslation":"Moreover, I'm marrying 30,000 thợ săn","nllbTranslation":"Hơn nữa, thê tử của Nhậm An Lạc là ba vạn thủy sư.","selectedModel":"nllb","translation":"Hơn nữa, thê tử của Nhậm An Lạc là ba vạn thủy sư."},{"id":76,"startTime":"00:05:29,120","endTime":"00:05:32,080","text":"而他太子赔上的却是整个大靖","libreTranslation":"Nhưng cái mà hoàng tử của ông phải trả là toàn bộ Đại Kinh.","opusTranslation":"Nhưng anh ta đã trả quá nhiều tiền cho nó.","mbartTranslation":"Và khi ông ta plead guilty, thì mọi người đều cảm thấy hài lòng","nllbTranslation":"mà thái tử của hắn lại phải trả toàn bộ Đại Tĩnh.","selectedModel":"nllb","translation":"mà thái tử của hắn lại phải trả toàn bộ Đại Tĩnh."},{"id":77,"startTime":"00:05:49,840","endTime":"00:05:52,400","text":"那靖南女水匪竟妄想做太子妃？","libreTranslation":"Nữ tặc nước Kinh Nam đó lại muốn làm thái tử?","opusTranslation":"Vậy là bọn hải tặc Nam Triều đã muốn làm Thái Phi?","mbartTranslation":"Người phụ nữ Yau Nam có ý định trở thành nữ hoàng?","nllbTranslation":"Vậy Tĩnh Nam nữ thủy tặc lại mơ tưởng làm thái tử phi?","selectedModel":"nllb","translation":"Vậy Tĩnh Nam nữ thủy tặc lại mơ tưởng làm thái tử phi?"},{"id":78,"startTime":"00:05:53,960","endTime":"00:05:55,520","text":"最近弄得满城风雨啊","libreTranslation":"Gần đây nó đã gây ra rất nhiều rắc rối trong thành phố.","opusTranslation":"Gần đây anh đã làm tràn ngập thành phố rồi.","mbartTranslation":"Gần đây đã có một cơn bão toàn bộ thành phố","nllbTranslation":"Gần đây có cả thành mưa gió.","selectedModel":"nllb","translation":"Gần đây có cả thành mưa gió."},{"id":79,"startTime":"00:05:55,600","endTime":"00:05:57,200","text":"连说书先生都在说这档子事","libreTranslation":"Ngay cả người kể chuyện cũng đang nói về chuyện này","opusTranslation":"Thậm chí ngay cả ngài sách cũng đang kể về chuyện này.","mbartTranslation":"Cũng như Mr. sách đang nói về file này","nllbTranslation":"Thậm chí Thư tiên sinh cũng đang nói về chuyện này.","selectedModel":"nllb","translation":"Thậm chí Thư tiên sinh cũng đang nói về chuyện này."},{"id":80,"startTime":"00:05:57,280","endTime":"00:05:58,400","text":"不过在狩猎宴上","libreTranslation":"Nhưng tại bữa tiệc đi săn","opusTranslation":"Nhưng ở đây là buổi tiệc săn.","mbartTranslation":"Nhưng trong bữa ăn săn","nllbTranslation":"Nhưng trong tiệc săn bắn,","selectedModel":"nllb","translation":"Nhưng trong tiệc săn bắn,"},{"id":81,"startTime":"00:05:58,480","endTime":"00:05:59,600","text":"女水匪身手不错","libreTranslation":"Nữ cướp nước khá giỏi","opusTranslation":"Cô gái thủy nữ rất giỏi.","mbartTranslation":"Người đàn ông: Cô là một tay cướp biển.","nllbTranslation":"Nữ Thủy Tẫn khỏe mạnh,","selectedModel":"nllb","translation":"Nữ Thủy Tẫn khỏe mạnh,"},{"id":82,"startTime":"00:05:59,680","endTime":"00:06:01,080","text":"-长得也不错\\n-华真","libreTranslation":"-Trông cũng được đấy.\\n-Hua Zhen","opusTranslation":"- Và cậu ấy cũng đẹp nữa.","mbartTranslation":"-It's not too long.","nllbTranslation":"- Đẹp lắm. - Hoa chân thật.","selectedModel":"nllb","translation":"- Đẹp lắm. - Hoa chân thật."},{"id":83,"startTime":"00:06:03,360","endTime":"00:06:05,640","text":"-属下在\\n-今日带回来的犯人","libreTranslation":"-Cấp dưới đang ở đây.\\n-Hôm nay tù nhân được đưa về","opusTranslation":"- Tù nhân đã mang về hôm nay.","mbartTranslation":"- The con man I brought back today","nllbTranslation":"- Thuộc hạ ở đây. - Vụ phạm hôm nay đưa về","selectedModel":"nllb","translation":"- Thuộc hạ ở đây. - Vụ phạm hôm nay đưa về"},{"id":84,"startTime":"00:06:05,720","endTime":"00:06:06,880","text":"处理得如何了？","libreTranslation":"Mọi chuyện thế nào rồi?","opusTranslation":"- Anh làm việc thế nào rồi?","mbartTranslation":"What's going on?","nllbTranslation":"xử lý thế nào rồi?","selectedModel":"nllb","translation":"xử lý thế nào rồi?"},{"id":85,"startTime":"00:06:06,960","endTime":"00:06:07,960","text":"回禀洛大人","libreTranslation":"Báo cáo lại cho anh Lữ","opusTranslation":"Bẩm đại nhân...","mbartTranslation":"Người Thổ Nhĩ Kỳ","nllbTranslation":"Bẩm Lạc đại nhân.","selectedModel":"nllb","translation":"Bẩm Lạc đại nhân."},{"id":86,"startTime":"00:06:08,040","endTime":"00:06:09,840","text":"铁瓮已经放在刑房了","libreTranslation":"Chiếc bình sắt đã được đặt trong phòng tra tấn","opusTranslation":"Cái bình đã được cất trong phòng giam rồi.","mbartTranslation":"Mũng đã được đặt trong nhà tù","nllbTranslation":"đã đặt sắt ngọc vào phòng xử tội rồi.","selectedModel":"nllb","translation":"đã đặt sắt ngọc vào phòng xử tội rồi."},{"id":87,"startTime":"00:06:09,920","endTime":"00:06:11,480","text":"不过一层皮而已","libreTranslation":"Chỉ là một lớp da","opusTranslation":"Nhưng chỉ là một lớp da nhỏ.","mbartTranslation":"Nhưng chỉ là một lớp da","nllbTranslation":"Nhưng chỉ là một lớp da thôi.","selectedModel":"nllb","translation":"Nhưng chỉ là một lớp da thôi."},{"id":88,"startTime":"00:06:12,800","endTime":"00:06:13,960","text":"你吩咐下去","libreTranslation":"Bạn ra lệnh","opusTranslation":"Ông cứ tiếp tục đi.","mbartTranslation":"\\"You've ordered","nllbTranslation":"Cô ra lệnh.","selectedModel":"nllb","translation":"Cô ra lệnh."},{"id":89,"startTime":"00:06:14,520","endTime":"00:06:15,920","text":"将他装入瓮中","libreTranslation":"Đặt anh ta vào một chiếc bình","opusTranslation":"Rồi nhét hắn vào lọ.","mbartTranslation":"Thắt him vào lọng","nllbTranslation":"để hắn vào trong lồng.","selectedModel":"nllb","translation":"để hắn vào trong lồng."},{"id":90,"startTime":"00:06:16,640","endTime":"00:06:18,720","text":"塞满碎冰 几个时辰后拔出来","libreTranslation":"Đổ đầy đá bào vào và lấy ra sau vài giờ.","opusTranslation":"Chẳng ai lấy nó ra sau vài giờ nữa.","mbartTranslation":"Sắp đầy ice. Bắt ra vài giờ sau","nllbTranslation":"đầy băng vỡ, vài canh giờ sau rút ra.","selectedModel":"nllb","translation":"đầy băng vỡ, vài canh giờ sau rút ra."},{"id":91,"startTime":"00:06:19,760","endTime":"00:06:21,360","text":"保证他脱得干干净净","libreTranslation":"Đảm bảo anh ấy cởi bỏ quần áo hoàn toàn","opusTranslation":"Đảm bảo hắn sạch sẽ.","mbartTranslation":"đảm bảo he wash sạch","nllbTranslation":"đảm bảo hắn sẽ sạch sẽ.","selectedModel":"nllb","translation":"đảm bảo hắn sẽ sạch sẽ."},{"id":92,"startTime":"00:06:23,000","endTime":"00:06:24,920","text":"-是\\n-下去吧","libreTranslation":"-Ừ\\n-Đi xuống","opusTranslation":"Xuống đi.","mbartTranslation":"- Yeah.","nllbTranslation":"- Vâng. - Lui xuống đi.","selectedModel":"nllb","translation":"- Vâng. - Lui xuống đi."},{"id":93,"startTime":"00:06:34,280","endTime":"00:06:36,440","text":"（吾归）","libreTranslation":"(tôi quay lại)","opusTranslation":"Ta về nhà.","mbartTranslation":"(Hãy trở về)","nllbTranslation":"TIU","selectedModel":"nllb","translation":"TIU"},{"id":94,"startTime":"00:06:39,000","endTime":"00:06:40,040","text":"梓元","libreTranslation":"Tử Nguyên","opusTranslation":"Yên Lạc","mbartTranslation":"Yuan","nllbTranslation":"Tử Nguyên.","selectedModel":"nllb","translation":"Tử Nguyên."},{"id":95,"startTime":"00:07:40,840","endTime":"00:07:41,760","text":"洛铭西","libreTranslation":"Lạc Minh Tây","opusTranslation":"- Đó là một lời thách thức.","mbartTranslation":"Lo Mingxi","nllbTranslation":"Lạc Minh Tây.","selectedModel":"nllb","translation":"Lạc Minh Tây."},{"id":96,"startTime":"00:07:44,320","endTime":"00:07:45,960","text":"认不出我了吗？","libreTranslation":"Bạn không nhận ra tôi sao?","opusTranslation":"Không nhận ra tôi sao?","mbartTranslation":"Bạn có nhận ra tôi không?","nllbTranslation":"Không nhận ra ta sao?","selectedModel":"nllb","translation":"Không nhận ra ta sao?"},{"id":97,"startTime":"00:07:47,800","endTime":"00:07:48,640","text":"不是","libreTranslation":"KHÔNG","opusTranslation":"Không.","mbartTranslation":"Không","nllbTranslation":"Không phải.","selectedModel":"nllb","translation":"Không phải."},{"id":98,"startTime":"00:07:50,400","endTime":"00:07:51,440","text":"我只是在想","libreTranslation":"Tôi chỉ đang nghĩ","opusTranslation":"Tôi chỉ đang suy nghĩ.","mbartTranslation":"Tôi chỉ đang nghĩ","nllbTranslation":"Ta chỉ đang nghĩ","selectedModel":"nllb","translation":"Ta chỉ đang nghĩ"},{"id":99,"startTime":"00:07:52,520","endTime":"00:07:53,760","text":"我该叫你梓元","libreTranslation":"Tôi nên gọi bạn là Ziyuan","opusTranslation":"Tôi nên gọi anh là Nguyên Giáp.","mbartTranslation":"Tôi nên gọi bạn là Yuan","nllbTranslation":"Ta nên gọi ngươi là Nguyên.","selectedModel":"nllb","translation":"Ta nên gọi ngươi là Nguyên."},{"id":100,"startTime":"00:07:54,320","endTime":"00:07:55,720","text":"还是叫你安乐？","libreTranslation":"Hay bạn muốn được hạnh phúc?","opusTranslation":"Hay là...","mbartTranslation":"Hay là bạn được an ủi?","nllbTranslation":"hay là gọi cô là An Lạc?","selectedModel":"nllb","translation":"hay là gọi cô là An Lạc?"},{"id":101,"startTime":"00:07:56,280","endTime":"00:07:59,280","text":"与韩家有关的名字就不要再提了","libreTranslation":"Đừng nhắc đến những cái tên liên quan đến họ Hàn nữa.","opusTranslation":"Đừng nhắc đến tên nhà Han nữa.","mbartTranslation":"Những tên liên quan đến gia đình Han sẽ không được nhắc đến","nllbTranslation":"đừng nhắc đến tên liên quan đến Hàn gia nữa.","selectedModel":"nllb","translation":"đừng nhắc đến tên liên quan đến Hàn gia nữa."},{"id":102,"startTime":"00:08:00,040","endTime":"00:08:01,440","text":"叫我安乐","libreTranslation":"gọi tôi là hòa bình","opusTranslation":"Tên tôi là Ant- gõ.","mbartTranslation":"Hãy gọi tôi là Anle","nllbTranslation":"gọi ta là An Lạc.","selectedModel":"nllb","translation":"gọi ta là An Lạc."},{"id":103,"startTime":"00:08:02,200","endTime":"00:08:03,600","text":"任安乐","libreTranslation":"Nhậm An Lê","opusTranslation":"Cho một chút lạc quan.","mbartTranslation":"Nian Le","nllbTranslation":"Nhậm An Lạc.","selectedModel":"nllb","translation":"Nhậm An Lạc."},{"id":104,"startTime":"00:08:05,120","endTime":"00:08:06,040","text":"好","libreTranslation":"Tốt","opusTranslation":"Được rồi.","mbartTranslation":"Tốt.","nllbTranslation":"Được.","selectedModel":"nllb","translation":"Được."},{"id":105,"startTime":"00:08:06,840","endTime":"00:08:07,800","text":"安乐","libreTranslation":"yên tâm","opusTranslation":"- Bình tĩnh nào.","mbartTranslation":"An-le","nllbTranslation":"An Lạc.","selectedModel":"nllb","translation":"An Lạc."},{"id":106,"startTime":"00:08:08,880","endTime":"00:08:10,600","text":"“任他清风明月","libreTranslation":"“Hãy để anh là gió và trăng","opusTranslation":"Hãy trừng phạt hắn.","mbartTranslation":"\\"Let him hoà bình","nllbTranslation":"Trẫm cho hắn thanh gió, minh trăng.","selectedModel":"nllb","translation":"Trẫm cho hắn thanh gió, minh trăng."},{"id":107,"startTime":"00:08:11,360","endTime":"00:08:12,960","text":"我自安康常乐”","libreTranslation":"Tôi sẽ luôn hạnh phúc và khỏe mạnh.”","opusTranslation":"Tôi tự giải trí.","mbartTranslation":"\\"I'm at ease","nllbTranslation":"ta tự an khang bình an,","selectedModel":"nllb","translation":"ta tự an khang bình an,"},{"id":108,"startTime":"00:08:15,120","endTime":"00:08:16,880","text":"这可是你给我取的名字","libreTranslation":"Đây là cái tên bạn đặt cho tôi","opusTranslation":"Đó là tên anh đặt cho tôi.","mbartTranslation":"Đây là tên mà bạn đặt cho tôi","nllbTranslation":"Đây là tên mà cô cho ta.","selectedModel":"nllb","translation":"Đây là tên mà cô cho ta."},{"id":109,"startTime":"00:08:18,800","endTime":"00:08:21,280","text":"（不避斧钺）","libreTranslation":"(Đừng tránh rìu)","opusTranslation":"KHÔNG CÓ LỐI ĐE DỌA","mbartTranslation":"(Cái đuôi)","nllbTranslation":"NGUYN THƯNG","selectedModel":"nllb","translation":"NGUYN THƯNG"},{"id":110,"startTime":"00:08:21,360","endTime":"00:08:22,440","text":"这几年间","libreTranslation":"vài năm này","opusTranslation":"Vài năm sau,","mbartTranslation":"trong vài năm","nllbTranslation":"Mấy năm nay,","selectedModel":"nllb","translation":"Mấy năm nay,"},{"id":111,"startTime":"00:08:22,920","endTime":"00:08:24,560","text":"你在朝 我在野","libreTranslation":"Bạn chịu trách nhiệm và tôi phản đối","opusTranslation":"- Anh đang tiến về phía trước. - Tôi đang ở giữa rừng.","mbartTranslation":"You're in the wild","nllbTranslation":"Ngươi ở triều, ta ở trong rừng.","selectedModel":"nllb","translation":"Ngươi ở triều, ta ở trong rừng."},{"id":112,"startTime":"00:08:24,640","endTime":"00:08:27,360","text":"辛苦你为我争取十年养精蓄锐的时间","libreTranslation":"Cảm ơn sự chăm chỉ của bạn, bạn đã cho tôi mười năm thời gian để hồi phục và hồi phục.","opusTranslation":"Anh đã dành 10 năm để cho tôi thu thập tài năng.","mbartTranslation":"Bạn đã dành 10 năm cho tôi để nuôi dưỡng tinh thần","nllbTranslation":"cô vất vả giành cho ta mười năm để nuôi dưỡng tinh lưu,","selectedModel":"nllb","translation":"cô vất vả giành cho ta mười năm để nuôi dưỡng tinh lưu,"},{"id":113,"startTime":"00:08:27,880","endTime":"00:08:29,360","text":"那还得多亏帝家家主","libreTranslation":"Tất cả là nhờ người đứng đầu nhà họ Di","opusTranslation":"Thế thì phải còn nhiều nhà hiền triết nữa.","mbartTranslation":"Cũng có nhiều tiền cho người chủ của nhà","nllbTranslation":"thì phải nhờ gia chủ Đế gia.","selectedModel":"nllb","translation":"thì phải nhờ gia chủ Đế gia."},{"id":114,"startTime":"00:08:29,840","endTime":"00:08:32,280","text":"让我将帝家之剑献给韩仲远","libreTranslation":"Hãy để tôi cống hiến thanh kiếm hoàng gia cho Han Zhongyuan","opusTranslation":"Hãy để ta dâng kiếm cho Hàn Trọng Đại chiến.","mbartTranslation":"để tôi trao cho Han Chun Sword","nllbTranslation":"để ta dâng kiếm Đế gia cho Hàn Trọng Viễn.","selectedModel":"nllb","translation":"để ta dâng kiếm Đế gia cho Hàn Trọng Viễn."},{"id":115,"startTime":"00:08:33,080","endTime":"00:08:34,080","text":"若非这样","libreTranslation":"Nếu không thì","opusTranslation":"Nếu không phải thế...","mbartTranslation":"Nếu không,","nllbTranslation":"Nếu không như vậy,","selectedModel":"nllb","translation":"Nếu không như vậy,"},{"id":116,"startTime":"00:08:34,559","endTime":"00:08:36,640","text":"我又怎会如此年轻就官拜刑部尚书？","libreTranslation":"Làm sao tôi có thể trở thành bộ trưởng của Bộ Hình phạt khi còn trẻ như vậy?","opusTranslation":"Sao ta lại phải thờ ơ Bộ Tư pháp chứ?","mbartTranslation":"Làm sao tôi lại còn trẻ khi đi làm cho bộ tội phạm?","nllbTranslation":"Sao ta còn trẻ thế mà có thể làm Thượng thư Hình bộ?","selectedModel":"nllb","translation":"Sao ta còn trẻ thế mà có thể làm Thượng thư Hình bộ?"},{"id":117,"startTime":"00:08:40,280","endTime":"00:08:42,559","text":"如今你先是三万水师求聘","libreTranslation":"Bây giờ bạn đang nộp đơn xin tuyển dụng ba mươi nghìn sĩ quan hải quân trước tiên","opusTranslation":"Bây giờ anh là 30 vạn Thủy sư rồi.","mbartTranslation":"Bây giờ, bạn đã tìm được 30,000 thợ thuyền","nllbTranslation":"Bây giờ ngươi xin tuyển ba vạn thủy sư trước,","selectedModel":"nllb","translation":"Bây giờ ngươi xin tuyển ba vạn thủy sư trước,"},{"id":118,"startTime":"00:08:42,640","endTime":"00:08:44,320","text":"又在春狩宴上亮相","libreTranslation":"Lại xuất hiện tại Đại tiệc săn xuân","opusTranslation":"Lại có thêm ánh sáng chói lọi cho buổi tiệc xuân.","mbartTranslation":"Vàng appears at bữa tiệc mùa xuân","nllbTranslation":"lại xuất hiện trong tiệc săn mùa xuân.","selectedModel":"nllb","translation":"lại xuất hiện trong tiệc săn mùa xuân."},{"id":119,"startTime":"00:08:44,400","endTime":"00:08:45,960","text":"这京城已经传遍了","libreTranslation":"Tin tức đã lan truyền khắp thủ đô","opusTranslation":"Thành phố này đã truyền qua nhiều lần rồi.","mbartTranslation":"Thành phố này đã được truyền bá","nllbTranslation":"Kinh thành đã được truyền khắp nơi rồi.","selectedModel":"nllb","translation":"Kinh thành đã được truyền khắp nơi rồi."},{"id":120,"startTime":"00:08:46,040","endTime":"00:08:48,080","text":"有玳山上的帝梓元在","libreTranslation":"Có Hoàng đế Ziyuan trên núi Daishan","opusTranslation":"Những trụ trì ở núi non.","mbartTranslation":"Có Emperor Yuan ở Mại Chua","nllbTranslation":"Có Đế Tử Nguyên trên núi Phượng.","selectedModel":"nllb","translation":"Có Đế Tử Nguyên trên núi Phượng."},{"id":121,"startTime":"00:08:49,280","endTime":"00:08:52,040","text":"我就是贪恋太子美色的任安乐","libreTranslation":"Tôi là Ren Anle, người khao khát vẻ đẹp của hoàng tử","opusTranslation":"Ta là kẻ ham muốn thái độ và dễ chịu.","mbartTranslation":"\\"I'm hungry for the beauty of the princess","nllbTranslation":"Ta chính là Nhậm An Lạc tham lam sắc đẹp của thái tử.","selectedModel":"nllb","translation":"Ta chính là Nhậm An Lạc tham lam sắc đẹp của thái tử."},{"id":122,"startTime":"00:08:52,640","endTime":"00:08:54,960","text":"这场婚求得越是轰轰烈烈","libreTranslation":"Lời cầu hôn này ngày càng trở nên mạnh mẽ hơn","opusTranslation":"Đám cưới này càng cầu xin nhiều, thì đám cưới này càng mạnh mẽ hơn.","mbartTranslation":"The marriage was sought out","nllbTranslation":"hôn sự này càng được thổi thổi,","selectedModel":"nllb","translation":"hôn sự này càng được thổi thổi,"},{"id":123,"startTime":"00:08:55,040","endTime":"00:08:57,280","text":"我越是能做我要做的事","libreTranslation":"Tôi càng có thể làm tốt hơn những gì tôi làm","opusTranslation":"Tôi càng cố gắng làm những gì tôi cần làm.","mbartTranslation":"Tôi càng có thể làm những việc tôi phải làm","nllbTranslation":"ta càng làm được việc ta muốn làm,","selectedModel":"nllb","translation":"ta càng làm được việc ta muốn làm,"},{"id":124,"startTime":"00:09:01,440","endTime":"00:09:02,440","text":"安乐","libreTranslation":"yên tâm","opusTranslation":"- Bình tĩnh nào.","mbartTranslation":"An-le","nllbTranslation":"An Lạc.","selectedModel":"nllb","translation":"An Lạc."},{"id":125,"startTime":"00:09:04,040","endTime":"00:09:05,440","text":"这韩烨每隔三个月","libreTranslation":"Han Ye này, ba tháng một lần","opusTranslation":"Hàn Quốc này mấy tháng rồi","mbartTranslation":"Mỗi 3 tháng","nllbTranslation":"Hàn Diệp này cứ ba tháng một lần","selectedModel":"nllb","translation":"Hàn Diệp này cứ ba tháng một lần"},{"id":126,"startTime":"00:09:05,920","endTime":"00:09:08,120","text":"都会往玳山上送礼物和书信","libreTranslation":"Họ sẽ gửi quà và thư đến núi Đại Sơn","opusTranslation":"Họ mang quà và thư từ lên đồi.","mbartTranslation":"Cho gifts và thưs lên Mt Ta","nllbTranslation":"đều đưa quà và thư đến núi Phượng.","selectedModel":"nllb","translation":"đều đưa quà và thư đến núi Phượng."},{"id":127,"startTime":"00:09:08,920","endTime":"00:09:10,160","text":"十年未曾间断过","libreTranslation":"Mười năm không gián đoạn","opusTranslation":"10 năm chưa từng có chuyện đó.","mbartTranslation":"10 năm qua","nllbTranslation":"mười năm chưa từng gián đoạn.","selectedModel":"nllb","translation":"mười năm chưa từng gián đoạn."},{"id":128,"startTime":"00:09:11,480","endTime":"00:09:14,400","text":"如此便能消除\\n韩家对我帝家的灭门之恨？","libreTranslation":"Điều này sẽ loại bỏ\\nHàn gia hận thù muốn diệt vong Hoàng gia chúng ta?","opusTranslation":"Vậy nhà Hàn Quốc có thể xóa bỏ sự hận thù của hoàng gia ta không?","mbartTranslation":"Do đó có thể xoá bỏ sự ghét của nhà Han đối với nhà vua?","nllbTranslation":"Như vậy sẽ có thể xóa bỏ thù hận diệt môn của Hàn gia với Đế gia ta?","selectedModel":"nllb","translation":"Như vậy sẽ có thể xóa bỏ thù hận diệt môn của Hàn gia với Đế gia ta?"},{"id":129,"startTime":"00:09:16,560","endTime":"00:09:17,760","text":"洛铭西","libreTranslation":"Lạc Minh Tây","opusTranslation":"- Đó là một lời thách thức.","mbartTranslation":"Lo Mingxi","nllbTranslation":"Lạc Minh Tây.","selectedModel":"nllb","translation":"Lạc Minh Tây."},{"id":130,"startTime":"00:09:17,840","endTime":"00:09:20,440","text":"我是等了十年要复仇的任安乐","libreTranslation":"Tôi là Ren Anle, người đã chờ đợi mười năm để trả thù.","opusTranslation":"Tôi đã chờ 10 năm để trả thù.","mbartTranslation":"\\"I've been waiting 10 năm để revenge","nllbTranslation":"Ta đã đợi mười năm để trả thù Nhậm An Lạc.","selectedModel":"nllb","translation":"Ta đã đợi mười năm để trả thù Nhậm An Lạc."},{"id":131,"startTime":"00:09:21,120","endTime":"00:09:23,720","text":"不是等待太子殿下垂怜的帝梓元","libreTranslation":"Người đang chờ đợi sự thương xót của Thái tử điện hạ không phải là Tử Nguyên hoàng đế.","opusTranslation":"Không phải là bình nguyên liệu để chờ thái tử tử tử thương xót.","mbartTranslation":"Không phải là người đang chờ vị hoàng tử trân trọng","nllbTranslation":"không phải là Đế Tử Nguyên chờ thái tử điện hạ thương xót.","selectedModel":"nllb","translation":"không phải là Đế Tử Nguyên chờ thái tử điện hạ thương xót."},{"id":132,"startTime":"00:09:25,520","endTime":"00:09:27,040","text":"你确定已经准备好了吗？","libreTranslation":"Bạn có chắc là mình đã sẵn sàng?","opusTranslation":"Anh chắc chứ?","mbartTranslation":"Các bạn chắc các bạn đã sẵn sàng?","nllbTranslation":"Chàng chắc chắn đã chuẩn bị xong chưa?","selectedModel":"nllb","translation":"Chàng chắc chắn đã chuẩn bị xong chưa?"},{"id":133,"startTime":"00:09:30,040","endTime":"00:09:32,240","text":"我帝家上下横死之夜","libreTranslation":"Cái đêm mà mọi người trong gia đình hoàng gia của tôi chết một cách đau đớn","opusTranslation":"Đêm nhà ta bị giết","mbartTranslation":"Tối của Thổi Tôi","nllbTranslation":"Đêm Đế gia ta chết ở trên dưới,","selectedModel":"nllb","translation":"Đêm Đế gia ta chết ở trên dưới,"},{"id":134,"startTime":"00:09:32,720","endTime":"00:09:34,600","text":"我一日未曾忘记","libreTranslation":"Tôi chưa bao giờ quên","opusTranslation":"Tôi chưa từng quên một ngày.","mbartTranslation":"Tôi đã không quên một ngày","nllbTranslation":"Ta chưa từng quên","selectedModel":"nllb","translation":"Ta chưa từng quên"},{"id":135,"startTime":"00:09:35,760","endTime":"00:09:36,760","text":"韩仲远","libreTranslation":"Hàn Trung Nguyên","opusTranslation":"Hàn Trọng Trung Quốc.","mbartTranslation":"Han Nakayan","nllbTranslation":"Hàn Trọng Viễn.","selectedModel":"nllb","translation":"Hàn Trọng Viễn."},{"id":136,"startTime":"00:09:37,760","endTime":"00:09:39,080","text":"你可曾想到","libreTranslation":"bạn đã bao giờ nghĩ","opusTranslation":"Anh có từng nghĩ...","mbartTranslation":"Bạn đã từng nghĩ","nllbTranslation":"Ngươi có từng nghĩ","selectedModel":"nllb","translation":"Ngươi có từng nghĩ"},{"id":137,"startTime":"00:09:39,600","endTime":"00:09:44,360","text":"帝家孤女会在十年后归来见你？","libreTranslation":"Liệu cô bé mồ côi nhà Địch có quay lại gặp bạn sau mười năm nữa không?","opusTranslation":"Cô độc trong nhà sẽ gặp anh sau 10 năm nữa?","mbartTranslation":"Em gái của nhà vua sẽ quay lại gặp bạn sau 10 năm?","nllbTranslation":"Con gái duy nhất Đế gia sẽ về gặp ngài sau mười năm?","selectedModel":"nllb","translation":"Con gái duy nhất Đế gia sẽ về gặp ngài sau mười năm?"},{"id":138,"startTime":"00:09:49,080","endTime":"00:09:51,040","text":"调查的结果如何了？","libreTranslation":"Kết quả của cuộc điều tra là gì?","opusTranslation":"Việc điều tra đến đâu rồi?","mbartTranslation":"What are the results of the investigation?","nllbTranslation":"Kết quả điều tra thế nào rồi?","selectedModel":"nllb","translation":"Kết quả điều tra thế nào rồi?"},{"id":139,"startTime":"00:09:51,960","endTime":"00:09:52,960","text":"陛下","libreTranslation":"Bệ Hạ","opusTranslation":"Thưa bệ hạ.","mbartTranslation":"Thượng hoàng","nllbTranslation":"Bệ hạ.","selectedModel":"nllb","translation":"Bệ hạ."},{"id":140,"startTime":"00:09:53,480","endTime":"00:09:55,320","text":"任安乐与太子相遇","libreTranslation":"Ren Anle gặp hoàng tử","opusTranslation":"Cho an nguy gặp Thái tử.","mbartTranslation":"Nô-an-lơ gặp Prince","nllbTranslation":"Nhậm An Lạc gặp Thái tử,","selectedModel":"nllb","translation":"Nhậm An Lạc gặp Thái tử,"},{"id":141,"startTime":"00:09:55,400","endTime":"00:09:58,160","text":"以三万水师求嫁之事","libreTranslation":"Lời cầu hôn với ba mươi ngàn lính thủy đánh bộ","opusTranslation":"Ta sẽ lấy 30 vạn thủy sư cầu hôn.","mbartTranslation":"Bao gồm 30,000 thuỷ sư","nllbTranslation":"Với chuyện ba vạn thủy sư cầu hôn,","selectedModel":"nllb","translation":"Với chuyện ba vạn thủy sư cầu hôn,"},{"id":142,"startTime":"00:09:58,840","endTime":"00:10:01,920","text":"与太子所述分毫不差","libreTranslation":"Đúng như lời hoàng tử nói","opusTranslation":"Thái tử không khác gì lời đồn.","mbartTranslation":"Không có gì khác so với tác phẩm của chúa","nllbTranslation":"không khác gì những gì Thái tử nói.","selectedModel":"nllb","translation":"không khác gì những gì Thái tử nói."},{"id":143,"startTime":"00:10:04,280","endTime":"00:10:07,800","text":"这任安乐果真是奔着太子而来？","libreTranslation":"Ren Anle này thực sự đến vì hoàng tử sao?","opusTranslation":"Có phải đó là lý do tại sao một trái dâu rơi xuống đây?","mbartTranslation":"Đúng không, người đàn ông An-lô-công đã đến đây như một vị hoàng tử?","nllbTranslation":"Nhậm An Lạc quả thật chạy theo thái tử?","selectedModel":"nllb","translation":"Nhậm An Lạc quả thật chạy theo thái tử?"},{"id":144,"startTime":"00:10:07,880","endTime":"00:10:09,240","text":"是啊 陛下","libreTranslation":"Vâng, thưa bệ hạ","opusTranslation":"Vâng, thưa bệ hạ.","mbartTranslation":"Đúng vậy, Your Majesty","nllbTranslation":"Đúng vậy, bệ hạ.","selectedModel":"nllb","translation":"Đúng vậy, bệ hạ."},{"id":145,"startTime":"00:10:09,320","endTime":"00:10:10,640","text":"陛下","libreTranslation":"Bệ Hạ","opusTranslation":"Thưa bệ hạ.","mbartTranslation":"Thượng hoàng","nllbTranslation":"Bệ hạ.","selectedModel":"nllb","translation":"Bệ hạ."},{"id":146,"startTime":"00:10:10,720","endTime":"00:10:13,160","text":"现在上上下下都在议论","libreTranslation":"Bây giờ mọi người đang nói về nó","opusTranslation":"Cả hai đều bàn tán về vấn đề này.","mbartTranslation":"Bây giờ, mọi thứ đều được thảo luận","nllbTranslation":"Bây giờ mọi người đều bàn bạc.","selectedModel":"nllb","translation":"Bây giờ mọi người đều bàn bạc."},{"id":147,"startTime":"00:10:13,240","endTime":"00:10:16,920","text":"这个任安乐\\n究竟能不能当上太子妃呢？","libreTranslation":"Ren Anle này\\nLiệu cô ấy có thể trở thành công chúa không?","opusTranslation":"Thế có phải là hoàng tử của chúng ta không?","mbartTranslation":"Sẽ như thế nào mà người này có thể trở thành nữ hoàng?","nllbTranslation":"Nhậm An Lạc này rốt cuộc có thể làm thái tử phi không?","selectedModel":"nllb","translation":"Nhậm An Lạc này rốt cuộc có thể làm thái tử phi không?"},{"id":148,"startTime":"00:10:17,000","endTime":"00:10:18,360","text":"为何当不上？","libreTranslation":"Tại sao không?","opusTranslation":"Sao không?","mbartTranslation":"Tại sao không?","nllbTranslation":"Sao không được?","selectedModel":"nllb","translation":"Sao không được?"},{"id":149,"startTime":"00:10:19,120","endTime":"00:10:22,720","text":"太子殿下赠礼玳山十年未断","libreTranslation":"Mười năm Thái tử điện hạ tặng Đại Sơn không ngừng","opusTranslation":"Hoàng tử tử vẫn chưa mất tích 10 năm nay.","mbartTranslation":"Thương Hoàng tử đã không ngưng trao cho Quêu 10 năm","nllbTranslation":"Thái tử điện hạ tặng lễ tạ sơn mười năm chưa ngừng.","selectedModel":"nllb","translation":"Thái tử điện hạ tặng lễ tạ sơn mười năm chưa ngừng."},{"id":150,"startTime":"00:10:23,240","endTime":"00:10:24,280","text":"故此…","libreTranslation":"Vì thế…","opusTranslation":"Và giờ...","mbartTranslation":"Do đó...","nllbTranslation":"Vì vậy...","selectedModel":"nllb","translation":"Vì vậy..."},{"id":151,"startTime":"00:10:25,880","endTime":"00:10:28,800","text":"当年太祖想帝、韩一家","libreTranslation":"Lúc đó Taizu muốn làm hoàng đế và nhà Hán","opusTranslation":"Thái Tổ tiên nhớ gia đình Hàn Quốc","mbartTranslation":"Gia đình Han, Grandfather of Tang","nllbTranslation":"Năm đó thái tổ nghĩ đến Đế gia, Hàn gia,","selectedModel":"nllb","translation":"Năm đó thái tổ nghĩ đến Đế gia, Hàn gia,"},{"id":152,"startTime":"00:10:29,320","endTime":"00:10:30,880","text":"天下共治","libreTranslation":"Cùng nhau cai trị thế giới","opusTranslation":"Thiên hạ đệ nhất Kinh Kinh thảo","mbartTranslation":"Sự thống trị trên khắp thế giới","nllbTranslation":"thiên hạ cùng trị,","selectedModel":"nllb","translation":"thiên hạ cùng trị,"},{"id":153,"startTime":"00:10:32,920","endTime":"00:10:36,600","text":"可是朕继位还不足六年","libreTranslation":"Nhưng tôi chỉ mới kế vị ngai vàng chưa đầy sáu năm.","opusTranslation":"Nhưng ta vẫn chưa có vị trí nào trong vòng 6 năm qua.","mbartTranslation":"Nhưng tôi vẫn chưa đủ 6 năm","nllbTranslation":"Nhưng trẫm còn chưa tới sáu năm.","selectedModel":"nllb","translation":"Nhưng trẫm còn chưa tới sáu năm."},{"id":154,"startTime":"00:10:37,720","endTime":"00:10:39,920","text":"这帝家就举兵谋逆","libreTranslation":"Gia đình hoàng đế dấy quân nổi dậy","opusTranslation":"Nhà này đã tung ra một kế hoạch phản loạn.","mbartTranslation":"The emperor conspire against","nllbTranslation":"Đế gia sẽ dồn binh mưu phản.","selectedModel":"nllb","translation":"Đế gia sẽ dồn binh mưu phản."},{"id":155,"startTime":"00:10:40,960","endTime":"00:10:43,240","text":"偏偏太子端着先帝的遗诏","libreTranslation":"Chuyện xảy ra là hoàng tử đang thực hiện sắc lệnh cuối cùng của cố hoàng đế.","opusTranslation":"Nó được làm từ tro cốt của hoàng đế.","mbartTranslation":"Đúng vậy, ông ta thốt lên lời tiên tri","nllbTranslation":"Thái tử đã có ý tưởng về di sản của tiên đế.","selectedModel":"nllb","translation":"Thái tử đã có ý tưởng về di sản của tiên đế."},{"id":156,"startTime":"00:10:44,240","endTime":"00:10:48,800","text":"将这帝家的孤女硬生生地给护住了","libreTranslation":"Anh đã mạnh mẽ bảo vệ cô gái mồ côi này của gia đình Hoàng đế.","opusTranslation":"Giữ cho cô gái này sống ở nơi hoang vu.","mbartTranslation":"Giving shelter to the emperor","nllbTranslation":"bảo vệ đất đai sinh ra của con gái duy nhất Đế gia.","selectedModel":"nllb","translation":"bảo vệ đất đai sinh ra của con gái duy nhất Đế gia."},{"id":157,"startTime":"00:10:50,240","endTime":"00:10:52,160","text":"这让朕真是难做","libreTranslation":"Điều này khiến tôi thực sự khó thực hiện","opusTranslation":"Ta thật không thích hợp khi để trẫm làm việc này.","mbartTranslation":"Điều này khiến tôi khó làm","nllbTranslation":"Làm vậy thật khó.","selectedModel":"nllb","translation":"Làm vậy thật khó."},{"id":158,"startTime":"00:10:52,240","endTime":"00:10:53,080","text":"（《女匪与太子》）","libreTranslation":"(\\"Nữ cướp và hoàng tử\\")","opusTranslation":"Đông Đức và thái tử","mbartTranslation":"(Nguy Thiệt và Hoàng tử)","nllbTranslation":"TN NC và Thái Tử C","selectedModel":"nllb","translation":"TN NC và Thái Tử C"},{"id":159,"startTime":"00:11:02,440","endTime":"00:11:03,840","text":"还是这个有趣啊","libreTranslation":"Điều này vẫn còn thú vị","opusTranslation":"Cái này cũng hay đấy.","mbartTranslation":"Sẽ là thú vị","nllbTranslation":"Hay là thú vị này.","selectedModel":"nllb","translation":"Hay là thú vị này."},{"id":160,"startTime":"00:11:07,280","endTime":"00:11:08,280","text":"赵福","libreTranslation":"Triệu Phủ","opusTranslation":"Triệu Phúc!","mbartTranslation":"Zhao Fu","nllbTranslation":"Phượng Phúc.","selectedModel":"nllb","translation":"Phượng Phúc."},{"id":161,"startTime":"00:11:08,360","endTime":"00:11:09,240","text":"奴才在","libreTranslation":"Nô lệ ở đây","opusTranslation":"Nô tài ở đây.","mbartTranslation":"Người đàn ông ở đây","nllbTranslation":"Nô tài mới ở đây.","selectedModel":"nllb","translation":"Nô tài mới ở đây."},{"id":162,"startTime":"00:11:09,800","endTime":"00:11:11,080","text":"传朕的旨意","libreTranslation":"truyền đạt ý chí của tôi","opusTranslation":"Truyền đạt ý muốn của trẫm.","mbartTranslation":"Ý của tôi","nllbTranslation":"Truyền ý chỉ của trẫm","selectedModel":"nllb","translation":"Truyền ý chỉ của trẫm"},{"id":163,"startTime":"00:11:11,800","endTime":"00:11:14,200","text":"明日让这个任安乐来见见朕","libreTranslation":"Hãy để Ren Anle đến gặp tôi vào ngày mai","opusTranslation":"Ngày mai ta sẽ cho người này thấy ta là người hùng.","mbartTranslation":"Hãy để người này gặp tôi tomorrow","nllbTranslation":"Ngày mai cho Nhậm An Lạc đến gặp cha.","selectedModel":"nllb","translation":"Ngày mai cho Nhậm An Lạc đến gặp cha."},{"id":164,"startTime":"00:11:14,840","endTime":"00:11:16,040","text":"朕倒要看看","libreTranslation":"tôi muốn xem nó","opusTranslation":"Ta muốn xem nó!","mbartTranslation":"Tôi sẽ xem","nllbTranslation":"Trẫm muốn xem.","selectedModel":"nllb","translation":"Trẫm muốn xem."},{"id":165,"startTime":"00:11:16,120","endTime":"00:11:20,280","text":"这个口口声声\\n想成为太子妃的靖南水匪","libreTranslation":"Cái này vừa miệng\\nTên cướp nước Kinh Nam muốn trở thành thái tử","opusTranslation":"Tiếng nói này muốn làm hoàng hậu Thái Hậu của Thái Hậu.","mbartTranslation":"Giọng nói này muốn trở thành nữ hoàng của Yasinh","nllbTranslation":"Cái tiếng nói này muốn trở thành Tĩnh Nam Thủy Tần của thái tử phi.","selectedModel":"nllb","translation":"Cái tiếng nói này muốn trở thành Tĩnh Nam Thủy Tần của thái tử phi."},{"id":166,"startTime":"00:11:21,720","endTime":"00:11:26,360","text":"究竟能不能降住\\n我这个冥顽不化的皇儿","libreTranslation":"Nó có thể được hạ xuống?\\nTôi là một hoàng tử bướng bỉnh","opusTranslation":"Rốt cuộc có thể hạ xuống 1 vị vua không, một vị vua ngoan đạo?","mbartTranslation":"Sẽ có thể giải quyết được con trai dối của tôi","nllbTranslation":"rốt cuộc có thể hạ mình không? Hoàng tử không tiến hóa của ta","selectedModel":"nllb","translation":"rốt cuộc có thể hạ mình không? Hoàng tử không tiến hóa của ta"},{"id":167,"startTime":"00:11:27,360","endTime":"00:11:29,160","text":"（不避斧钺）","libreTranslation":"(Đừng tránh rìu)","opusTranslation":"KHÔNG CÓ LỐI ĐE DỌA","mbartTranslation":"(Cái đuôi)","nllbTranslation":"NGUYN THƯNG","selectedModel":"nllb","translation":"NGUYN THƯNG"},{"id":168,"startTime":"00:11:29,240","endTime":"00:11:31,800","text":"这古云年坑杀帝家八万将士","libreTranslation":"Trong năm mây cổ này, 80.000 binh sĩ hoàng gia đã bị giết trong một cái bẫy","opusTranslation":"Bát Thập Tam đã bị giết bởi Quan tướng Thiên Vân Trường.","mbartTranslation":"Vài năm qua, 80,000 generals of Emperor đã bị huỷ diệt","nllbTranslation":"Cổ Vân Niên giết tám vạn tướng sĩ Đế gia,","selectedModel":"nllb","translation":"Cổ Vân Niên giết tám vạn tướng sĩ Đế gia,"},{"id":169,"startTime":"00:11:32,560","endTime":"00:11:34,240","text":"又害帝家诛九族","libreTranslation":"Anh ta còn làm hại gia đình hoàng đế và giết chết chín bộ tộc","opusTranslation":"Ngươi đã hại nhà ngươi 9 người.","mbartTranslation":"Đoạn Xúng đã huỷ diệt nhà vua","nllbTranslation":"lại hại Đế gia, Tử Cửu tộc.","selectedModel":"nllb","translation":"lại hại Đế gia, Tử Cửu tộc."},{"id":170,"startTime":"00:11:34,320","endTime":"00:11:35,560","text":"他罪不可赦","libreTranslation":"Tội lỗi của anh ta không thể tha thứ","opusTranslation":"Tội không thể tha thứ.","mbartTranslation":"Anh ta không thể tha thứ","nllbTranslation":"tội của hắn không thể tha.","selectedModel":"nllb","translation":"tội của hắn không thể tha."},{"id":171,"startTime":"00:11:36,160","endTime":"00:11:38,720","text":"韩仲远却念他戕害帝家有功","libreTranslation":"Han Zhongyuan cho rằng mình đã góp phần vào việc sát hại gia đình hoàng đế.","opusTranslation":"Hàn Trọng Trung đã xem trọng công lực của ông ấy","mbartTranslation":"Han cho rằng ông đã làm hại gia đình","nllbTranslation":"Hàn Trọng Viễn lại nhớ hắn có công hại Đế gia.","selectedModel":"nllb","translation":"Hàn Trọng Viễn lại nhớ hắn có công hại Đế gia."},{"id":172,"startTime":"00:11:38,800","endTime":"00:11:41,480","text":"放任他权倾朝野 众臣攀附","libreTranslation":"Hãy để anh ta thống trị chính phủ và công chúng, và tất cả các quan chức sẽ bám lấy anh ta","opusTranslation":"Để nó không bị sụp đổ, họ sẽ ủng hộ nó.","mbartTranslation":"để cho quyền của ông trèo lên.","nllbTranslation":"để hắn có quyền đi theo triều đình. Các thần đồng ý,","selectedModel":"nllb","translation":"để hắn có quyền đi theo triều đình. Các thần đồng ý,"},{"id":173,"startTime":"00:11:42,120","endTime":"00:11:45,040","text":"不过韩烨和右相魏谏","libreTranslation":"Tuy nhiên, Han Ye và hữu tướng Wei Jian","opusTranslation":"Nhưng Hàn Bình và Bên Phải...","mbartTranslation":"Nhưng Han Yi và Yu Wei","nllbTranslation":"Nhưng Hàn Diệp và Hữu tướng gia","selectedModel":"nllb","translation":"Nhưng Hàn Diệp và Hữu tướng gia"},{"id":174,"startTime":"00:11:45,840","endTime":"00:11:47,040","text":"行的是正道","libreTranslation":"Làm đúng cách","opusTranslation":"Chính xác.","mbartTranslation":"Giving on the right","nllbTranslation":"làm đúng đường.","selectedModel":"nllb","translation":"làm đúng đường."},{"id":175,"startTime":"00:11:47,120","endTime":"00:11:48,360","text":"跟他势同水火","libreTranslation":"Cùng đẳng cấp với anh ấy","opusTranslation":"Anh ta cũng bị thiêu sống như anh ta.","mbartTranslation":"Giọng với anh ta.","nllbTranslation":"cùng với hắn.","selectedModel":"nllb","translation":"cùng với hắn."},{"id":176,"startTime":"00:11:48,920","endTime":"00:11:50,640","text":"你以求取太子妃入局","libreTranslation":"Bạn vào game để giành được vương miện công chúa","opusTranslation":"Ngươi đòi hỏi Thái tử nhập ngũ.","mbartTranslation":"\\"You'll go to the imperial court","nllbTranslation":"Ngươi muốn lấy thái tử phi vào trận,","selectedModel":"nllb","translation":"Ngươi muốn lấy thái tử phi vào trận,"},{"id":177,"startTime":"00:11:51,480","endTime":"00:11:54,160","text":"所以这太子当是同盟","libreTranslation":"Vậy vị hoàng tử này nên là đồng minh","opusTranslation":"Vậy thái tử là đồng minh.","mbartTranslation":"Vì vậy, Prince này là một đồng minh","nllbTranslation":"Vậy nên thái tử này coi như đồng minh.","selectedModel":"nllb","translation":"Vậy nên thái tử này coi như đồng minh."},{"id":178,"startTime":"00:11:54,240","endTime":"00:11:56,000","text":"（烨、帝）","libreTranslation":"(Vâng, Hoàng đế)","opusTranslation":"Nhìn này.","mbartTranslation":"(Chúa, Em)","nllbTranslation":"THÁI, TIM","selectedModel":"nllb","translation":"THÁI, TIM"},{"id":179,"startTime":"00:11:56,600","endTime":"00:11:58,560","text":"那下一步便借力打力","libreTranslation":"Vậy thì hãy tận dụng sức mạnh của chúng ta ở bước tiếp theo","opusTranslation":"Vậy thì hãy dùng lực của mình.","mbartTranslation":"Sau đó, hãy cố gắng","nllbTranslation":"Vậy bước tiếp theo là mượn sức,","selectedModel":"nllb","translation":"Vậy bước tiếp theo là mượn sức,"},{"id":180,"startTime":"00:12:07,280","endTime":"00:12:08,480","text":"大理寺？","libreTranslation":"Chùa Đại Lý?","opusTranslation":"Đại Lý Tự?","mbartTranslation":"Thảm đài?","nllbTranslation":"Đại lý tự?","selectedModel":"nllb","translation":"Đại lý tự?"},{"id":181,"startTime":"00:12:09,080","endTime":"00:12:11,360","text":"这大理寺由古云年把持","libreTranslation":"Ngôi chùa Đại Lý này do Cố Vân Niên kiểm soát","opusTranslation":"Đại Lý Tự là một di sản từ những năm xưa.","mbartTranslation":"Lầy đền này được quản lý từ thời Ai","nllbTranslation":"Đại Lý tự do này do Cổ Vân Niên giữ giữ,","selectedModel":"nllb","translation":"Đại Lý tự do này do Cổ Vân Niên giữ giữ,"},{"id":182,"startTime":"00:12:11,840","endTime":"00:12:13,280","text":"拿捏各家后路","libreTranslation":"Tìm ra các lựa chọn của mỗi gia đình","opusTranslation":"Mang mấy cái dây chuyền kia theo.","mbartTranslation":"Bắt chén cho từng gia đình","nllbTranslation":"lấy lưng đường của các gia tộc","selectedModel":"nllb","translation":"lấy lưng đường của các gia tộc"},{"id":183,"startTime":"00:12:13,920","endTime":"00:12:15,480","text":"所以你的第一步","libreTranslation":"Vì vậy bước đầu tiên của bạn","opusTranslation":"Vậy bước đầu tiên của cậu là gì?","mbartTranslation":"Vậy bước đầu tiên của bạn","nllbTranslation":"Vậy nên bước đầu tiên của cô","selectedModel":"nllb","translation":"Vậy nên bước đầu tiên của cô"},{"id":184,"startTime":"00:12:16,040","endTime":"00:12:18,160","text":"就是要拜大理寺少卿","libreTranslation":"Tôi chỉ muốn tỏ lòng tôn kính vị thừa tướng trẻ của chùa Đại Lý","opusTranslation":"Muốn bái nghĩa chủ nghĩa","mbartTranslation":"\\"It's about the Master of Tai-ri","nllbTranslation":"là phải bái đại lý tự thiếu khanh.","selectedModel":"nllb","translation":"là phải bái đại lý tự thiếu khanh."},{"id":185,"startTime":"00:12:19,480","endTime":"00:12:22,480","text":"最好还能有一个案子做引","libreTranslation":"Tốt nhất nên có một trường hợp để tham khảo","opusTranslation":"Tôi nghĩ tôi có thể có một vụ nữa.","mbartTranslation":"Tốt nhất là có một trường hợp được đưa ra","nllbTranslation":"Tốt nhất là có thể có một vụ án làm lí do.","selectedModel":"nllb","translation":"Tốt nhất là có thể có một vụ án làm lí do."},{"id":186,"startTime":"00:12:22,560","endTime":"00:12:25,000","text":"深入到古云年的党羽之中","libreTranslation":"Đi sâu vào các thành viên trong nhóm của Gu Yunian","opusTranslation":"Một người thông minh hơn mình.","mbartTranslation":"Giọng sâu vào thời Tang","nllbTranslation":"sâu vào phe Vân Cổ Vân.","selectedModel":"nllb","translation":"sâu vào phe Vân Cổ Vân."},{"id":187,"startTime":"00:12:25,080","endTime":"00:12:27,720","text":"我刑部正有一个案子","libreTranslation":"Có một vụ án đang xảy ra ở sở hình sự của chúng tôi","opusTranslation":"Tôi có một vụ ở Bộ hình sự.","mbartTranslation":"B: I've got a case","nllbTranslation":"Hình bộ ta đang có một vụ án.","selectedModel":"nllb","translation":"Hình bộ ta đang có một vụ án."},{"id":188,"startTime":"00:12:28,560","endTime":"00:12:30,000","text":"林聪杀人案","libreTranslation":"Vụ án giết người Lin Cong","opusTranslation":"Vụ giết người ở Lâm khờ.","mbartTranslation":"vụ giết người của Lin-chung","nllbTranslation":"Lâm thông minh giết người,","selectedModel":"nllb","translation":"Lâm thông minh giết người,"},{"id":189,"startTime":"00:12:31,400","endTime":"00:12:33,200","text":"爹爹 求求你","libreTranslation":"Bố ơi, làm ơn","opusTranslation":"Cha, làm ơn đi!","mbartTranslation":"Chà, Chà","nllbTranslation":"Cha, xin cha.","selectedModel":"nllb","translation":"Cha, xin cha."},{"id":190,"startTime":"00:12:33,280","endTime":"00:12:35,480","text":"让大理寺把林聪放了吧","libreTranslation":"Hãy để chùa Dali thả Lin Cong.","opusTranslation":"Hãy để cho Đại Lý Tự thoát khỏi sự đau khổ của mình.","mbartTranslation":"Hãy để Temple cho Lin biết.","nllbTranslation":"bảo Đại Lý Tự thả Lâm Thông ra đi.","selectedModel":"nllb","translation":"bảo Đại Lý Tự thả Lâm Thông ra đi."},{"id":191,"startTime":"00:12:35,560","endTime":"00:12:37,880","text":"林聪将一名监生活活打死","libreTranslation":"Lâm Thông đánh chết tù nhân","opusTranslation":"Lâm khờ đã giết chết một người lính canh","mbartTranslation":"Lin cho một người bị giam tử","nllbTranslation":"Lâm Thông đánh chết một giám sát.","selectedModel":"nllb","translation":"Lâm Thông đánh chết một giám sát."},{"id":192,"startTime":"00:12:37,960","endTime":"00:12:41,280","text":"-可他…\\n-太子和刑部都盯上这事了","libreTranslation":"-Nhưng anh ấy...\\n- Hoàng tử và Bộ Hình phạt đều chú ý đến vấn đề này.","opusTranslation":"Thái tử và bộ tra tấn đã chú ý đến chuyện này.","mbartTranslation":"-But he-- -The Prince and the Department of Penitentiary have been keeping watch.","nllbTranslation":"- Nhưng hắn... - Thái tử và Hình bộ đều đồn chuyện này rồi.","selectedModel":"nllb","translation":"- Nhưng hắn... - Thái tử và Hình bộ đều đồn chuyện này rồi."},{"id":193,"startTime":"00:12:41,840","endTime":"00:12:44,040","text":"林聪他爹这些年","libreTranslation":"Cha của Lin Cong đã","opusTranslation":"Cha của Lâm khờ.","mbartTranslation":"Những năm của Cha","nllbTranslation":"Lâm trí đã trêu chọc mấy năm nay,","selectedModel":"nllb","translation":"Lâm trí đã trêu chọc mấy năm nay,"},{"id":194,"startTime":"00:12:44,720","endTime":"00:12:47,040","text":"可为你做了不少体己的事","libreTranslation":"Tôi đã làm rất nhiều điều chu đáo cho bạn","opusTranslation":"Nhưng tôi đã làm nhiều điều cho cô.","mbartTranslation":"Nhưng tôi đã làm rất nhiều việc cho bạn","nllbTranslation":"nhưng đã làm việc không ít thân phận cho ngươi.","selectedModel":"nllb","translation":"nhưng đã làm việc không ít thân phận cho ngươi."},{"id":195,"startTime":"00:12:48,480","endTime":"00:12:49,760","text":"侯爷…","libreTranslation":"Ông Hầu…","opusTranslation":"Hầu gia...","mbartTranslation":"Ông...","nllbTranslation":"Hầu gia...","selectedModel":"nllb","translation":"Hầu gia..."},{"id":196,"startTime":"00:12:52,920","endTime":"00:12:53,920","text":"李由","libreTranslation":"Lý Hữu","opusTranslation":"Lý do.","mbartTranslation":"Lee","nllbTranslation":"Lý Do.","selectedModel":"nllb","translation":"Lý Do."},{"id":197,"startTime":"00:12:54,680","endTime":"00:12:55,520","text":"在","libreTranslation":"hiện hữu","opusTranslation":"Vâng.","mbartTranslation":"ở","nllbTranslation":"Có.","selectedModel":"nllb","translation":"Có."},{"id":198,"startTime":"00:12:55,600","endTime":"00:12:57,640","text":"去大理寺跟裴沾说一声","libreTranslation":"Đến chùa Đại Lý và nói chuyện với Pei Zhan","opusTranslation":"Đi đến Đại Lý Tự nói với Lão Lão","mbartTranslation":"Go to Tai Temple và nói với Pei","nllbTranslation":"đến Đại Lý tự nói với cha một tiếng.","selectedModel":"nllb","translation":"đến Đại Lý tự nói với cha một tiếng."},{"id":199,"startTime":"00:12:58,200","endTime":"00:13:00,240","text":"让他把那个林聪从刑部捞出来","libreTranslation":"Hãy để anh ta đưa Lin Cong ra khỏi Cục Hình sự","opusTranslation":"Bảo hắn đào cái hộp đó ra khỏi phòng tra tấn.","mbartTranslation":"Giving him to get Lin out of jail","nllbTranslation":"bảo hắn cứu Lâm Thông ra khỏi Hình bộ.","selectedModel":"nllb","translation":"bảo hắn cứu Lâm Thông ra khỏi Hình bộ."},{"id":200,"startTime":"00:13:00,800","endTime":"00:13:01,720","text":"是","libreTranslation":"Đúng","opusTranslation":"Vâng.","mbartTranslation":"Vâng","nllbTranslation":"Vâng.","selectedModel":"nllb","translation":"Vâng."},{"id":201,"startTime":"00:13:03,440","endTime":"00:13:05,680","text":"刚才听到了一件趣事","libreTranslation":"Tôi vừa nghe được một điều thú vị","opusTranslation":"Tôi vừa nghe một tin tức thú vị.","mbartTranslation":"Có một điều thú vị vừa được nghe","nllbTranslation":"Vừa nãy ta nghe được một chuyện thú vị.","selectedModel":"nllb","translation":"Vừa nãy ta nghe được một chuyện thú vị."},{"id":202,"startTime":"00:13:06,720","endTime":"00:13:07,680","text":"任安乐","libreTranslation":"Nhậm An Lê","opusTranslation":"Cho một chút lạc quan.","mbartTranslation":"Nian Le","nllbTranslation":"Nhậm An Lạc.","selectedModel":"nllb","translation":"Nhậm An Lạc."},{"id":203,"startTime":"00:13:09,080","endTime":"00:13:12,280","text":"那个大肆宣扬求嫁太子的女水匪","libreTranslation":"Nữ thủy tặc thổi kèn cầu hôn hoàng tử","opusTranslation":"Tình trạng khẩn cầu đám cưới thái hậu.","mbartTranslation":"Người đàn ông: The thieves who boast about marrying the chúa","nllbTranslation":"Tên nữ thủy tặc bừa bãi tuyên bố xin cưới thái tử đó","selectedModel":"nllb","translation":"Tên nữ thủy tặc bừa bãi tuyên bố xin cưới thái tử đó"},{"id":204,"startTime":"00:13:13,480","endTime":"00:13:16,440","text":"不过听说是个美人","libreTranslation":"Nhưng tôi nghe nói cô ấy là một người đẹp","opusTranslation":"Nhưng nghe nói là mỹ nhân.","mbartTranslation":"Nhưng tôi biết cô ấy là một người đẹp","nllbTranslation":"Nhưng nghe nói là một mỹ nhân,","selectedModel":"nllb","translation":"Nhưng nghe nói là một mỹ nhân,"},{"id":205,"startTime":"00:13:16,520","endTime":"00:13:18,320","text":"这要是能收来做个妾室…","libreTranslation":"Nếu tôi có thể coi đây là vợ lẽ...","opusTranslation":"Nếu như có thể làm thiếp của ta","mbartTranslation":"Sẽ là một concubine.","nllbTranslation":"Nếu có thể thu nhận để làm phòng bếp...","selectedModel":"nllb","translation":"Nếu có thể thu nhận để làm phòng bếp..."},{"id":206,"startTime":"00:13:18,400","endTime":"00:13:20,480","text":"满脑子就这点东西","libreTranslation":"Đó là tất cả những gì tôi nghĩ trong đầu","opusTranslation":"Đó là tất cả thứ trong đầu tôi.","mbartTranslation":"Mấy ý tưởng về điều này","nllbTranslation":"toàn bộ đầu óc là cái này.","selectedModel":"nllb","translation":"toàn bộ đầu óc là cái này."},{"id":207,"startTime":"00:13:20,560","endTime":"00:13:21,600","text":"我…","libreTranslation":"TÔI…","opusTranslation":"Tôi...","mbartTranslation":"Tôi...","nllbTranslation":"Ta...","selectedModel":"nllb","translation":"Ta..."},{"id":208,"startTime":"00:13:24,000","endTime":"00:13:26,240","text":"三万水师不容小觑","libreTranslation":"Ba vạn hải quân không thể coi thường","opusTranslation":"Ba vạn Nước là thứ không thể khinh rẻ được.","mbartTranslation":"30,000 thợ lặn","nllbTranslation":"Ba vạn thủy sư không chịu tiểu tẩu.","selectedModel":"nllb","translation":"Ba vạn thủy sư không chịu tiểu tẩu."},{"id":209,"startTime":"00:13:26,320","endTime":"00:13:28,280","text":"哪怕求不得太子妃","libreTranslation":"Ngay cả khi tôi không thể yêu cầu một công chúa","opusTranslation":"Có lẽ không cầu quá nhiều cho một con dâu.","mbartTranslation":"Chẳng có con gái nào","nllbTranslation":"Dù không thể cầu xin thái tử phi,","selectedModel":"nllb","translation":"Dù không thể cầu xin thái tử phi,"},{"id":210,"startTime":"00:13:28,360","endTime":"00:13:30,640","text":"陛下也必会委以要职","libreTranslation":"Bệ hạ cũng sẽ giao phó cho ông những chức vụ quan trọng","opusTranslation":"Đức vua cũng sẽ phải chịu trách nhiệm.","mbartTranslation":"Thượng hoàng cũng sẽ được bổ nhiệm","nllbTranslation":"Bệ hạ cũng nhất định sẽ giao cho ngài.","selectedModel":"nllb","translation":"Bệ hạ cũng nhất định sẽ giao cho ngài."},{"id":211,"startTime":"00:13:31,240","endTime":"00:13:32,920","text":"若我没有猜错","libreTranslation":"Nếu tôi đoán đúng","opusTranslation":"Nếu tôi không nhầm...","mbartTranslation":"Nếu tôi không đoán sai","nllbTranslation":"Nếu ta không đoán nhầm,","selectedModel":"nllb","translation":"Nếu ta không đoán nhầm,"},{"id":212,"startTime":"00:13:33,840","endTime":"00:13:37,800","text":"大靖恐怕是要有第二个女官了","libreTranslation":"Tôi sợ Đại Kinh sẽ có nữ quan thứ hai.","opusTranslation":"Đại Tĩnh, ta sợ rằng sẽ có quan chức thứ hai.","mbartTranslation":"Oh Yau, I'm afraid there's a con gái","nllbTranslation":"e là Đại Tĩnh sắp có nữ quan thứ hai rồi.","selectedModel":"nllb","translation":"e là Đại Tĩnh sắp có nữ quan thứ hai rồi."},{"id":213,"startTime":"00:13:37,880","endTime":"00:13:40,400","text":"第一个帝家不也全都没了吗？","libreTranslation":"Chẳng phải Địch gia thứ nhất cũng đi rồi sao?","opusTranslation":"Không phải tất cả các vị tiên đều bị thế sao?","mbartTranslation":"Gia đình đầu tiên không còn gì cả?","nllbTranslation":"Đế gia đầu tiên cũng không hết sao?","selectedModel":"nllb","translation":"Đế gia đầu tiên cũng không hết sao?"},{"id":214,"startTime":"00:13:40,480","endTime":"00:13:42,920","text":"如今也只剩下一个孤女","libreTranslation":"Giờ chỉ còn lại một đứa trẻ mồ côi","opusTranslation":"Giờ chỉ còn lại một cô gái.","mbartTranslation":"Giờ đây, chỉ có một cô bé","nllbTranslation":"bây giờ chỉ còn lại một cô nương.","selectedModel":"nllb",